import { Icon } from "@iconify/vue";
let _alertid = 0;
export default {
  props: {
    background: {
      type: String,
      default: ""
    },
    demo: {
      type: Boolean,
      default: false
    },
    border: {
      type: Boolean,
      default: false
    },
    borderColor: {
      type: String
    },
    textColor: {
      type: String
    },
    actionClose: {
      type: Boolean,
      default: false
    }
  },

  data() {
    _alertid += 1;
    return {
      close: false,
      _id: `alert-id-${_alertid}`
    };
  },

  components: {
    Icon
  },
  methods: {
    demoAlert() {
      setTimeout(() => {
        this.close = false;
      }, 2000);
    },

    closeAlert() {
      this.close = true;

      if (this.demo == true) {
        this.demoAlert();
      }
    }

  },

  mounted() {}

};
